import React, { useState, useEffect } from "react";
import UserDataService from "../services/UserService";
import "./User.css";

const User = (props) => {
  const initialUserState = {
    id: null,
    country: "",
    email: "",
    name: "",
  };
  const [currentUser, setCurrentUser] = useState(initialUserState);
  const [message, setMessage] = useState("");
  const [showButtons, setShowButtons] = useState(false); // New state for button visibility

  const getUser = (id) => {
    UserDataService.get(id)
      .then((response) => {
        setCurrentUser(response.data);
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    getUser(props.match.params.id);
  }, [props.match.params.id]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setCurrentUser({ ...currentUser, [name]: value });
  };

  const updateUser = () => {
    UserDataService.update(currentUser.id, currentUser)
      .then((response) => {
        console.log(response.data);
        setMessage("The User was updated successfully!");
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const deleteUser = () => {
    UserDataService.remove(currentUser.id)
      .then((response) => {
        console.log(response.data);
        props.history.push("/Users");
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    // Show buttons after a delay
    const timeout = setTimeout(() => {
      setShowButtons(true);
    }, 500);
    return () => clearTimeout(timeout);
  }, []);

  return (
    <div>
      {currentUser ? (
        <div className="edit-form">
          <h4>User</h4>
          <form>
            {/* ...form inputs... */}
          </form>

          {/* Fade-in animation for buttons */}
          <div className={`button-animation ${showButtons ? "show" : ""}`}>
            <button
              type="button"
              onClick={deleteUser}
              className="btn btn-danger btn-sm"
            >
              Delete
            </button>
            &nbsp;
            <button
              type="button"
              onClick={updateUser}
              className="btn btn-success btn-sm"
            >
              Update
            </button>
          </div>

          <strong>
            <p className="text-success">{message}</p>
          </strong>
        </div>
      ) : (
        <div>
          <br />
          <p>Click on a User...</p>
        </div>
      )}
    </div>
  );
};

export default User;
